script(                'js-pretty-photo',                get_template_directory_uri( ) . '/media/js/jquery.prettyPhoto.js'            );            wp_enqueue_script(                'settings-pretty-photo',                get_template_directory_uri( ) . '/media/js/settings.prettyPhoto.js'            );            wp_enqueue_style(                'css-pretty-photo',                get_template_directory_uri( ) . '/media/css/prettyPhoto.css'            );                        if( is_home() || is_front_page() ){                if( myTheme::get( 'under-construction' ) ){                    wp_enqueue_script(                        'functions',                        get_template_directory_uri( ) . '/media/js/functions.js'                    );                }            }            /* INCLUDE FOR REPLY COMMENTS */            if ( is_singular() ){                wp_enqueue_script( "comment-reply" );            }            wp_head();        ?>            </head>    <body <?php body_class( ); ?>>                <?php            if( is_front_page() ){                if( myTheme::get( 'under-construction' ) ){                    get_template_part( 'cfg/templates/view/under-construction' );                    exit();                }            }        ?>        <div class="wrapper">            <header>                <div class="line">                    <div class="temp medium to-left">                        <hgroup>                            <?php if( myTheme::get( 'logo' ) ) { ?>                                <a href="<?php echo home_url(); ?>" title="<?php bloginfo( 'name' ); echo ' '; bloginfo( 'description' ); ?>">                                    <img src="<?php echo myTheme::get( 'logo' ); ?>" alt="<?php bloginfo( 'name' ); echo ' '; bloginfo( 'description' ); ?>"/>                                </a>                            <?php }else { ?>                                <?php if( is_home() || is_front_page() ) { ?>                                    <h1><a href="<?php echo home_url(); ?>" title="<?php bloginfo( 'name' ); echo ' '; bloginfo( 'description' ); ?>"><?php bloginfo( 'name' ); ?></a></h1>                                <?php }else{ ?>                                    <h2><a href="<?php echo home_url(); ?>" title="<?php bloginfo( 'name' ); echo ' '; bloginfo( 'description' ); ?>"><?php bloginfo( 'name' ); ?></a></h2>                                <?php } ?>                                <p><a href="<?php echo home_url(); ?>" title="<?php bloginfo( 'name' ); echo ' '; bloginfo( 'description' ); ?>"><?php bloginfo( 'description' ); ?></a></p>                            <?php } ?>                        </hgroup>                    </div>                    <div class="aside large to-right">                        <div class="social">                            <?php if( myTheme::get( 'rss' ) ) { ?>                                    <a href="<?php bloginfo('rss2_url'); ?>" class="rss" target="_blank"></a>                            <?php } ?>                            <?php if( strlen( esc_url( myTheme::get( 'youtube' ) ) ) ) { ?>                                <a href="<?php echo myTheme::get( 'youtube' ); ?>" class="youtube" target="_blank"></a>                            <?php } ?>                            <?php if( strlen( esc_url( myTheme::get( 'google-plus' ) ) ) ) { ?>                                <a href="<?php echo esc_url( myTheme::get( 'facebook' ) ); ?>" class="google-plus" target="_blank"></a>                            <?php } ?>                            <?php if( strlen( esc_url( myTheme::get( 'facebook' ) ) ) ) { ?>                                <a href="<?php echo esc_url( myTheme::get( 'facebook' ) ); ?>" class="facebook" target="_blank"></a>                            <?php } ?>                            <?php if( strlen( esc_url( myTheme::get( 'twitter' ) ) ) ) { ?>                             